import React, { Component } from "react";
import $ from 'jquery';
import InputCustomizado from './InputCustomizado';
import PubSub from 'pubsub-js';
import TratadorErros from "./TratadorErros";


class FormularioLivro extends Component {
    constructor(){
        super();
        this.state = {titulo:'',preco:'',autor:''};
        this.enviaForm = this.enviaForm.bind(this);
        this.setNome = this.setTitulo.bind(this);
        this.setEmail = this.setPreco.bind(this);
        this.setSenha = this.setAutor.bind(this);
      }

      render(){
          return(
            <div className="pure-form pure-form-aligned">

                <form className="pure-form pure-form-aligned" onSubmit={this.enviaForm} methot="post">
                <InputCustomizado id="titulo" type="text" name="titulo" value={this.state.titulo} onChange={this.setTitulo} label="Titulo"/>
                <InputCustomizado id="preco" type="text" name="email" value={this.state.preco} onChange={this.setPreco} label="PreÃ§o"/>
                <InputCustomizado id="autor" type="select" name="autor" value={this.state.autor} onChange={this.setAutor} label="Autor"/>
                <div className="pure-control-group">
                <InputCustomizado id="submit" type="submit" name="submit" value="Gravar" className="pure-button pure-button-primary"/>
                </div>
                </form>

            </div>
          );
      }


      enviaForm(evento){
        evento.preventDefault();
        console.log("dados sendo gravados")
        $.ajax({
          url:"https://cdc-react.herokuapp.com/api/livros",
          contentType: 'application/json',
          dataType:'json',
          type: 'post',
          data: JSON.stringify({titulo:this.state.titulo,preco:this.state.preco,autor:this.state.autor}),
          success:function(resposta){
            PubSub.publish('atualiza-lista-livros', resposta);
            this.setState({titulo:'',preco:'',autor:''});
          }.bind(this),
          error:function(resposta){
            if(resposta.status === 400) {
              new TratadorErros().publicaErros(resposta.responseJSON);
            }
          },
          beforeSend: function(){
            PubSub.publish("limpa-erros",{});
          }
        });
      }
      setTitulo(evento){  
        this.setState({titulo:evento.target.value});
      }

      setPreco(evento){
        this.setState({preco:evento.target.value});
      }

      setAutor(evento){
        this.setState({autor:evento.target.value});
      }
}

class TabelaLivros extends Component {

      render() {
          return(
            <div>
            <table className="pure-table">
              <thead>
                <tr>
                  <th>Nome</th>
                  <th>email</th>
                </tr>
              </thead>
                <tbody>
                        {
                        this.props.lista.map(function(autor){
                            return (
                            <tr key={autor.id}>
                                <td>{autor.nome}</td>
                                <td>{autor.email}</td>
                            </tr>
                            );
                        })
                        }
                </tbody>
            </table> 
          </div>  
          );
      }
}

export default class AutorBox extends Component {
    constructor(){
        super();
        this.state = {lista : []};  
    }

    componentDidMount(){
        $.ajax({
          url:"http://cdc-react.herokuapp.com/api/autores",
          dataType: 'json',
          success:function(resposta){
            console.log(this);
            this.setState({lista:resposta});
          }.bind(this)
        });

        PubSub.subscribe('atualiza-lista-autores', function(topico, novaLista){
          this.setState({lista:novaLista});
        }.bind(this));
    }

    render(){
        return(
            <>
            <div className="header">
                <h1>Cadastro de Autores</h1>
            </div>
            <div className="content" id="content">
            <FormularioAutor/>

            <TabelaAutores lista={this.state.lista}/>
            </div>
            </>
        );
    }
}
