import React, { Component } from 'react';
import './css/pure-min.css';
import './css/pure-sidebar.css';
import './css/pure-sidebar.css';
import $ from 'jquery';
import InputCustomizado from './components/InputCustomizado'
import Autores from './components/Autores';


class App extends Component {


  render() {
    return (
      <>
      <div id="menu">
        <div className="pure-menu">
          <a className="pure-menu-heading" href="#company">Company</a>

          <ul className="pure-menu-list">
            <li className="pure-menu-item"><a href="#home" className="pure-menu-link">Home</a></li>
            <li className="pure-menu-item"><a href="#home" className="pure-menu-link">Autor</a></li>
            <li className="pure-menu-item"><a href="#home" className="pure-menu-link">Livro</a></li>
          </ul>
        </div>
      </div>
      <div id="main">
            <div className="header">
              <h1>Cadastro de Autores</h1>
            </div>
            <div className="content" id="content">
              <div className="pure-form pure-form-aligned">
                <form className="pure-form pure-form-aligned" onSubmit={this.enviaForm} methot="post">
                  <InputCustomizado id="nome" type="text" name="nome" value={this.state.nome} onChange={this.setNome} label="Nome"/>
                  <InputCustomizado id="email" type="email" name="email" value={this.state.email} onChange={this.setEmail} label="Email"/>
                  <InputCustomizado id="senha" type="password" name="senha" value={this.state.senha} onChange={this.setSenha} label="Senha"/>
                  <div className="pure-control-group">
                  <InputCustomizado id="submit" type="submit" name="submit" value="Gravar" className="pure-button pure-button-primary"/>
                  </div>
                </form>

              </div>
              <div>
                <table className="pure-table">
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>email</th>
                    </tr>
                  </thead>
                  <tbody>
                    {
                      this.state.lista.map(function(autor){
                        return (
                          <tr key={autor.id}>
                            <td>{autor.nome}</td>
                            <td>{autor.email}</td>
                          </tr>
                        );
                      })
                    }
                  </tbody>
                </table> 
              </div>             
            </div>
          </div> 
        </>
    );
  }
}

export default App;
